import SocialCounter from "./controller/";

'use strict';
const e = eval, global: NodeJS.Global = e('this');

declare namespace NodeJS {
  interface Global {
    document: Document;
    window: Window;
    navigator: Navigator;
    socialShareCount: SocialCounter;
  }
}

if (typeof (module) !== 'undefined') {
  if (typeof (module).exports.socialShareCount === 'undefined') {
    (module).exports = SocialCounter;
  }
}

if (typeof (global) !== 'undefined') {
  if (typeof global.socialShareCount === 'undefined') {
    global.socialShareCount = new SocialCounter();
  }
}
