import Social    from "../store/Social";
import Facebook  from "../store/Facebook";
import Twitter   from "../store/Twitter";
import Google    from "../store/Google";
import Feedly    from "../store/Feedly";
import Pinterest from "../store/Pinterest";
import Linkedin  from "../store/Linkedin";
import Hatena    from "../store/Hatena";
import Pocket    from "../store/Pocket";

/**
 * SocialShareCount Model Class
 * @public
 **/
export class SocialCounterModel {
  public option;

  public createSocialCounter(social): Social {
    let socialModel;

    switch(social.name) {
      case 'facebook':
        socialModel = Facebook.fromData(social);
        break;
      case 'twitter':
        socialModel = Twitter.fromData(social);
        break;
      case 'google':
        if(this.option && this.option.google_key) {
          socialModel = Google.fromData(social, this.option.google_key);
        } else {
          socialModel = Google.fromData(social);
        }
        break;
      case 'feedly':
        socialModel = Feedly.fromData(social);
        break;
      case 'pinterest':
        socialModel = Pinterest.fromData(social);
        break;
      case 'linkedin':
        socialModel = Linkedin.fromData(social);
        break;
      case 'hatena':
        socialModel = Hatena.fromData(social);
        break;
      case 'pocket':
        socialModel = Pocket.fromData(social);
        break;
    }

    return socialModel;
  }

  public setOption(option) {
    this.option = option;
  }
}


export default SocialCounterModel;
